pragma solidity >=0.4.21 <0.7.0;

contract CampaignFactory {
    address[] public totalCampaigns;

    event NewCampaign(
        address indexed campaignAddress,
        address indexed ownerAddress,
        uint256 minimumValue
    );

    function createCampaign(uint256 _minimum, string memory _topic) public {
        Campaign newCampaign = new Campaign(_minimum, _topic, msg.sender);
        totalCampaigns.push(address(newCampaign));

        emit NewCampaign(address(newCampaign), msg.sender, _minimum);
    }

    function getTotalCampaigns() public view returns (address[] memory) {
        return totalCampaigns;
    }
}

contract Campaign {
    constructor(
        uint256 _minimun,
        string memory _topic,
        address _creator
    ) public {
        topic = _topic;
        minimumContribution = _minimun;
        owner = _creator;
    }

    string topic;
    uint256 minimumContribution;
    address public owner;
    mapping(address => bool) approvers;
    uint256 public approveCounts;
    RequestInfo[] public requests;

    struct RequestInfo {
        string description;
        uint256 value;
        uint256 approvalCounts;
        bool complete;
        address payable recipient;
        mapping(address => bool) approvals;
    }

    event Contribute(uint256 balance, uint256 approveCounts);
    event Request(
        string description,
        uint256 value,
        uint256 approvalCounts,
        bool complete,
        address payable recipient
    );

    modifier restricted() {
        require(msg.sender == owner, "You are not the owner");
        _;
    }

    function getMinmum() public view returns (uint256) {
        return minimumContribution;
    }

    function contribute() public payable {
        require(
            msg.value > minimumContribution,
            "donation must be more than the minimumContribution"
        );
        if (!approvers[msg.sender]) {
            approveCounts++;
        }
        approvers[msg.sender] = true;
        emit Contribute(address(this).balance, approveCounts);
    }

    function createRequest(
        string memory _description,
        uint256 _value,
        address payable _recipient
    ) public restricted {
        require(_value <= address(this).balance, "Too much value");
        RequestInfo memory newRequest = RequestInfo({
            description: _description,
            value: _value,
            recipient: _recipient,
            complete: false,
            approvalCounts: 0
        });

        requests.push(newRequest);
    }

    function approveRequest(uint256 _index) public {
        require(approvers[msg.sender], "You are not a contributor");
        RequestInfo storage request = requests[_index];
        require(
            !request.approvals[msg.sender],
            "You have aleady signed before"
        );

        request.approvals[msg.sender] = true;
        request.approvalCounts++;
    }

    function finalizeRequest(uint256 _index) public restricted {
        RequestInfo storage request = requests[_index];
        require(
            request.approvalCounts > (approveCounts / 2),
            "Not enough approvals"
        );
        require(!request.complete, "Thist request was completed");

        request.recipient.transfer(request.value);
        request.complete = true;
    }

    function getSummary()
        public
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            address,
            string memory
        )
    {
        return (
            minimumContribution,
            address(this).balance,
            requests.length,
            approveCounts,
            owner,
            topic
        );
    }

    function getRequestCounts() public view returns (uint256) {
        return requests.length;
    }
}
